    const [timer, setTime] = useState(1)
    const [activeWork, setActiveWork] = useState(images[0]) // sets initial work slideshow
    const [activeImgIdx, setActiveImgIdx] = useState(0)
    const [activeImg, setActiveImg] = useState(<img src={images[0][0]} />) // sets initial image value
    const [nextImg, setNextImg] = useState(0)

    useEffect(() => setTimeState(), [activeImgIdx]) // resets time state whenever image changes

    useEffect(() => {
        const img = new Image()
        img.src = images[0][1]
        img.onload = () => setActiveImg(img)
    }, [])

