import './Portfolio.css'
import React, {
    useState,
    useEffect
} from 'react'

import { images } from './imagesData'
import ArrowForward from '../chevron-forward-outline.svg'
import ArrowBack from '../chevron-back-outline.svg'

const Portfolio = () => {

    const [activeWork, setActiveWork] = useState(images[0]) // sets initial work slideshow
    const [activeImgIdx, setActiveImgIdx] = useState(0)
    const [slidesLoading, setSlidesLoading] = useState(false)
    const [imageSlides, setImageSlides] = useState()
    const [slideBtns, setSlideBtns] = useState()
    const [activeImg, setActiveImg] = useState()
    const [previousImg, setPreviousImg] = useState()

    useEffect( () => slidesLoading && addLoadingAnimation(), [slidesLoading] )
    useEffect( () => renderImages(), [activeWork])
    useEffect( () => imageSlides && setActiveImg(imageSlides[0]), [imageSlides] ) // sets initial slide
    useEffect( () => imageSlides && implementSlideshow(), [activeImg] ) // function to change index
    useEffect( () => handleImgChange(), [activeImgIdx]) // function to handle activeImgIdx
    useEffect( () => activeImg && addSlideshowAnimation(), [activeImg] ) // just adds animation
    useEffect(() => addScrollEffect(), [])

    const addLoadingAnimation = () => {
        const node = document.querySelector('.portfolio').querySelector('.img-container')
        const ellipsis = node.querySelectorAll('.loading-dot')

        ellipsis[0].classList.add('bounce')
        setTimeout(() => ellipsis[1].classList.add('bounce'), 667)
        setTimeout(() => ellipsis[2].classList.add('bounce'), 1333)
    }
    const renderImages = async () => {
        const node = document.querySelector('.portfolio').querySelector('.active-container')

        setSlidesLoading(true)

        const imgArr = activeWork.map( src => 
            <img
                src={src}
                alt='roadarlo website' 
                className='slideshow-img'
            />)
        const slidesBtnArr = []

        for ( let i = 0; i < imgArr.length; i++ ) {
            slidesBtnArr[i] = <div className='slide-btn' />
        }
        setImageSlides([...imgArr])
        setSlideBtns([...slidesBtnArr])
        setSlidesLoading(false)
    }
    const implementSlideshow = () => {
        const nextIdx = activeImgIdx === activeWork.length - 1 ?
            0 :
            activeImgIdx + 1

        setTimeout(() => {
            setActiveImgIdx( nextIdx )
        }, 2999) // sets the timer for 3s
    }
    const handleImgChange = () => {
        if ( imageSlides ) {
            const previousIdx = activeImgIdx === 0 ?
            imageSlides.length - 1 :
            activeImgIdx - 1

            activeImg && setPreviousImg( imageSlides[previousIdx] )
            setActiveImg( imageSlides[activeImgIdx] )
        }
    }
    const addSlideshowAnimation = () => {
        const node = document.querySelector('.portfolio').querySelector('.active-container')
        const imgs = node.querySelectorAll('.slideshow-img')

        imgs[0].classList.remove('slide-img-in-right')
        if ( imgs[1] ) {
            imgs[1].classList.remove('slide-img-out-left')
            imgs[1].style.position = 'absolute'
        }
        setTimeout(() => {
            imgs[0].classList.add('slide-img-in-right')
            if ( imgs[1] ) imgs[1].classList.add('slide-img-out-left')
        }, 1)
    }
    const addScrollEffect = () => {
        const node = document.querySelector('.App')
        const works = node.querySelector('.portfolio-container').querySelectorAll('.work-container')

        node.onwheel = () => {
            const appHeight = (node.clientHeight - window.innerHeight)
            const scrollPercent = (window.pageYOffset / appHeight) * 100
            const workPercent = 100 / works.length

            // minus 0.1 to ensure that the index never reaches 
            const scrollBehaviour = scrollPercent < 100 ?
                scrollPercent / workPercent :
                scrollPercent / workPercent - 0.1 // ensures value never reaches undefined index 
            const index = Math.floor( scrollBehaviour )

            works.forEach( work => {
                work.classList.remove('active-container')
            })
            works[index].classList.add('active-container')
        }
    }

    return (
        <div className='portfolio'>
            <h3>I don't have a lot, but it's something!</h3>
            <div className='portfolio-container'>
                <div className='active-container work-container'>
                    {activeWork === images[0] ? 
                        <div>
                            <div className='img-container'>
                                {slidesLoading && <div className='loading-animation'>
                                    <div className='loading-dot' />
                                    <div className='loading-dot' />
                                    <div className='loading-dot' />
                                </div>}
                                {!slidesLoading && activeImg}
                                {!slidesLoading && previousImg}
                            </div>
                            <div className='slides-btn-container'>
                                <img
                                    src={ArrowBack}
                                    alt='arrow back'
                                    className='icon'
                                />
                                {slideBtns}
                                <img
                                    src={ArrowForward}
                                    alt='arrow forward'
                                    className='icon'
                                />
                            </div>
                            <h2>RoadArlo</h2>
                            <a
                                href='https://roadarlo-client.herokuapp.com'
                                target='__blank'
                            >roadarlo-client.herokuapp.com</a>
                            <p>My very first full-stack application. This project was based off of an idea I had and it's still in the working prototype stage. The look is very basic(it was my first website... give me a break &#128517;), but I'm proud of the back-end work I did for this one.</p> 
                        </div> :
                        <div>
                            <div className='img-container'>
                                <img
                                    src={images[0][0]}
                                    alt='roadarlo website'
                                />
                            </div>
                            <h2>RoadArlo</h2>
                            <a
                                href='https://roadarlo-client.herokuapp.com'
                                target='__blank'
                            >roadarlo-client.herokuapp.com</a>
                            <p>My very first full-stack application. This project was based off of an idea I had and it's still in the working prototype stage. The look is very basic(it was my first website... give me a break &#128517;), but I'm proud of the back-end work I did for this one.</p> 
                        </div>
                    }
                </div>
            </div>
        </div>
    )
}

export default Portfolio
