import './Portfolio.css'
import React, {
    useState,
    useEffect
} from 'react'

import { images } from './imagesData'

const Portfolio = () => {

    const [timer, setTimer] = useState(1)
    const [activeWork, setActiveWork] = useState() // sets initial work slideshow
    const [activeImgIdx, setActiveImgIdx] = useState(0)
    const [images, setImages] = useState()
    const [activeImg, setActiveImg] = useState( <img 
        src={images[0][0]} 
        alt='roadarlo website' 
    />) // sets initial image value

    useEffect(() => setTimeState(), [activeImgIdx]) // resets time state whenever image changes
    useEffect(() => renderImages(), [timer])

    const setTimeState = () => {
        setTimer(1)
        setTimeout(() => setTimer(0), 3000) // sets the timer for 3s
    }
    const renderImages = async () => {
        const nextIdx = activeImgIdx === activeWork.length - 1 ?
            0 : 
            activeImgIdx + 1
        const img = React.createElement('img', {
            src: activeWork[nextIdx],
            alt: 'roadarlo website'
        })

        setNextImg(...img)
    }

    return (
        <div className='portfolio'>
            <h3>I don't have a lot, but it's something!</h3>
            <div className='active-container'>
                <div className='img-container'>
                    {activeImg}
                    {images}
                    {/* <img
                        src={nextImg}
                        className='next-img'
                    /> */}
                </div>
            </div>
        </div>
    )
}

export default Portfolio
