import './Portfolio.css'
import {
    useState,
    useEffect
} from 'react'

import raHome from '../roadarlo-client-images/1.png'
import raMap from '../roadarlo-client-images/2.png'
import raProduct from '../roadarlo-client-images/3.png'
import raVendor from '../roadarlo-client-images/4.png'

const Portfolio = () => {

    const [activeImg, setActiveImg] = useState(0)

    useEffect(() => addSlideFx(), [])
    useEffect(() => console.log(activeImg), [activeImg])

    const addSlideFx = () => {

        // had to do animations javascript, adding/removing class names
        // are adding bugs I can't figure out to fix

        const node = document.querySelector('.portfolio').querySelector('.img-container')
        const images = node.querySelectorAll('img')

        const slideIn = image => {
            image.style.animationName = 'slideImgIn'
            image.style.animationDuration = '2s'
            image.style.animationFillMode = 'forwards'
        }
        const slideOut = image => {
            image.style.animationName = 'slideImgOut'
            image.style.animationDuration = '2s'
            image.style.animationFillMode = 'forwards'

        }

        /* initial animation */
        images[0].style.animationName = 'slideImgIn'
        images[0].style.animationDuration = '2s'
        images[0].style.animationFillMode = 'forwards'

        node.onclick = () => {
            node.onmousemove = e => {

                if ( e.movementX < 0 ) {
                    const nextSlide = activeImg === images.length - 1 ? 
                        0 : 
                        activeImg + 1

                    slideOut( images[activeImg] )
                    slideIn( images[nextSlide] )
                    setActiveImg( nextSlide )
                } else if ( e.movementX > 0 ) {
                    slideOut( images[activeImg] )
                    /* if ( activeImg !== images.length-1 ) {
                        const nextSlide = activeImg--

                        slideIn( images[nextSlide] )
                        setActiveImg( nextSlide )
                    } else return */
                }
            }
        }
    }

    return (
        <div className='portfolio'>
            <h3>I don't have a lot, but it's something!</h3>
            <div className='work-container'>
                <div className='img-container'>
                    <img 
                        src={raHome} 
                        className='' // initial load
                        alt='roadarlo home page'
                        draggable={false}
                    />
                    <img
                        src={raMap}
                        alt='roadarlo map page'
                        draggable={false}
                    />
                    <img
                        src={raProduct}
                        alt='roadarlo product page'
                        draggable={false}
                    />
                    <img
                        src={raVendor}
                        alt='roadarlo vendor page'
                        draggable={false}
                    />
                </div>
            </div>
        </div>
    )
}

export default Portfolio
